import React from 'react';

class listenCebolla extends React.Component {
  handleChange(event) {
    this.setState({ value: event.target.value });
  }
  handleSubmit(event) {
    alert('An essay was submitted: ' + this.state.value);
    event.preventDefault();
  }

  render() {
    return (
      <form>
        <label>
          Escribe:
          <textarea value='' />
        </label>
      </form>
    );
  }
}
