import React from 'react';

class OnionHater extends React.Component {
  constructor(props) {
    super(props);
    this.forceUpdate();
    this.isHating = 'false';
    this.onChangeText = this.onChangeText.bind(this);
  }
  onChangeText = ev => {
    if (ev.target.value.includes('cebolla') || ev.target.value.includes('CEBOLLA')) {
      const label = ev.target.parentElement;
      label.parentElement.classList.add('red');

      this.isHating = 'true';
    } else {
      const label = ev.target.parentElement;
      label.parentElement.classList.remove('red');
      this.isHating = 'true';
    }
  };
  render() {
    return (
      <form className=''>
        <label>
          Escribe:
          <textarea onChange={this.onChangeText} />
        </label>
      </form>
    );
  }
}
export default OnionHater;
