// import { useState } from 'react';
import React from 'react';

const App = () => {
  // const [number, setNumber] = useState(20);
  // const [present, setPresent] = useState(1);
  // const increaseNumber = () => {
  //   setNumber(number + 1);
  // };
  // const increasePresents = () => {
  //   setPresents(present + 1);
  // };
  const handleChange = (ev) => {
    console.log(ev.target.value);
    const name = ev.target.value;
    return name;
  };
  return (
    <>
      <form className='minimo'>
        <label htmlFor='name'>Escribe el nombre de la película</label>
        <input name='name' type='text' placeholder='Nombre de la película' onChange={handleChange}></input>
        <label htmlFor='description'>Escribe la descripción de la película</label>
        <input name='description' type='textarea' placeholder='Descripción de la película' onChange={handleChange}></input>
        <label htmlFor='language'></label>
        <select onChange={handleChange}>
          <option>Inglés</option>
          <option>Español</option>
          <option>Italiano</option>
        </select>
      </form>
      <article>
        <h3></h3>
      </article>
    </>
  );
};

export default App;
