import React from 'react';
import Item from './Item';

const items = [
  {
    name: 'Cereales con chocolate',
    description: 'Cereales rellenos de chocolate',
    quantity: 2,
    category: 'Cereales',
    price: 5
  },
  {
    name: 'Hamburguesa con queso',
    description: 'Hamburguesa rica y saludable',
    quantity: 1,
    category: 'Fast-food',
    price: 15
  },
  {
    name: 'Agua mineral',
    description: 'Agua de un charco del Himalaya',
    quantity: 2,
    category: 'Bebida',
    price: 5
  }
];

class ItemListWithFor extends React.Component {
  render() {
    const cartItems = [];
    for (let i = 0; i < items.length; i++) {
      cartItems.push(<Item name={items[i].name} description={items[i].description} quantity={items[i].quantity} category={items[i].category} price={items[i].price} />);
    }
    return <ul>{cartItems}</ul>;
  }
}

class ItemListWithMapInArray extends React.Component {
  render() {
    const cartItems = items.map(item => {
      return <Item name={item.name} description={item.description} quantity={item.quantity} category={item.category} price={item.price} />;
    });
    return <ul>{cartItems}</ul>;
  }
}

class ItemListWithMapIntoReturn extends React.Component {
  render() {
    return (
      <ul>
        {items.map(item => {
          return <Item name={item.name} description={item.description} quantity={item.quantity} category={item.category} price={item.price} />;
        })}
      </ul>
    );
  }
}

const renderItems = () => {
  return items.map(item => {
    return <Item name={item.name} description={item.description} quantity={item.quantity} category={item.category} price={item.price} />;
  });
};
class ItemList extends React.Component {
  render() {
    return <ul>{renderItems()}</ul>;
  }
}

export default ItemList;
