import React from 'react';
import { useState } from 'react';
// import Card from './Card';
let listMapped = '';
const App = () => {
  const numbers = [1, 4, 6, 8, 45, 89];
  const [data, setData] = useState({
    value: '',
  });
  const handleValue = (ev) => {
    setData({ value: ev.currentTarget.value });
    getNumber();
  };
  let lis = numbers.map((number) => <li> {number}</li>);
  const getNumber = () => {
    lis = numbers.map((n) => n).filter((n) => <li> {n >= data.value}</li>);
  };

  return (
    <>
      <ul>{lis}</ul>
      <form>
        <label>Indica el nº a partir del cual deben mostrarse los números de la lista</label>
        <input type='text' value={data.value} onChange={handleValue}></input>
      </form>
      {/* <p>{listMapped}</p> */}
    </>
  );
};
export default App;
