import React from 'react';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.Text;
    this.handleText = this.handleText.bind(this);
  }
  handleText(ev) {
    this.Text.value = ev.target.value;
    return Text.value;
  }

  render() {
    return (
      <div>
        <input type='text' onKeyUp={this.handleText}></input>
        <p>{this.Text.value}</p>
      </div>
    );
  }
}

export default App;
