import { useState } from 'react';
import React from 'react';
import Card from './Card';

const App = () => {
  const [data, setData] = useState({
    name: '',
    description: '',
    lamguage: '',
  });
  // const [present, setPresent] = useState(1);
  // const increaseNumber = () => {
  //   setNumber(number + 1);
  // };
  // const increasePresents = () => {
  //   setPresents(present + 1);
  // };
  const handleName = (ev) => {
    setData({ name: ev.currentTarget.value });
    console.log(data.name);
  };

  const handleDescription = (ev) => {
    setData({
      name: handleName(),
      description: ev.currentTarget.value,
    });
    console.log(data.description);
  };
  const handleLanguage = (ev) => {
    setData({ language: ev.currentTarget.value });
    console.log(data.language);
  };
  return (
    <>
      <form className='minimo'>
        <label htmlFor='name'>Escribe el nombre de la película</label>
        <input name='name' type='text' placeholder='Nombre de la película' onChange={handleName} value={data.name}></input>
        <label htmlFor='description'>Escribe la descripción de la película</label>
        <input name='description' type='textarea' placeholder='Descripción de la película' onChange={handleDescription}></input>
        <label htmlFor='language'></label>
        <select onChange={handleLanguage}>
          <option>Inglés</option>
          <option>Español</option>
          <option>Italiano</option>
        </select>
      </form>
      <Card name={data.name} description={data.description} />
    </>
  );
};

export default App;
