import React from 'react';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: ''
    };
    this.handleText = this.handleText.bind(this);
  }
  handleText(ev) {
    this.setState((prevState, props) => {
      let Text = ev.target.value;
      return Text;
    });
  }

  render() {
    return (
      <div>
        <input type='text' onKeyUp={this.handleText}></input>
        <p>{this.Text}</p>
      </div>
    );
  }
}

export default App;
