const onChangeText = ev => {
  if (ev.target.value.includes('cebolla')) {
    const label = ev.target.parentElement;
    label.parentElement.classList.add('red');
    this.forceUpdate();
  }
};
class OnionHater extends React.Component {
  constructor(props) {
    super(props);
    this.onChangeText = this.onChangeText.bind(this);
  }
  render() {
    return (
      <form className='form'>
        <label>
          Escribe:
          <textarea onChange={onChangeText} />
        </label>
      </form>
    );
  }
}
